import React from 'react';

export default function IkPolitikasi() {
  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-4">İK Politikamız</h1>
      <img src="/images/ik-politikasi.png" alt="İK Politikası" className="w-full max-w-3xl mb-6" />
      <p className="text-gray-700 text-lg leading-relaxed">
        İnsan kaynakları politikamız; çalışan memnuniyetini artırmak, sürekli gelişimi desteklemek ve eşit fırsatlar sunmak üzerine kuruludur.
      </p>
    </div>
  );
}
