import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="p-6 bg-gray-100">
      <header className="text-center mb-10">
        <h1 className="text-4xl font-bold text-blue-800">TESCOM GÜÇ ELEKTRONİĞİNE HOŞ GELDİNİZ</h1>
        <p className="mt-4 text-lg text-gray-700">Kesintisiz güç kaynakları ve çözümlerimizle yanınızdayız.</p>
      </header>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
        <Link to="/ups" className="bg-white p-6 rounded-xl shadow hover:shadow-lg transition">
          <h2 className="text-xl font-semibold text-blue-700">UPS Sistemleri</h2>
          <p className="mt-2 text-gray-600">Yüksek performanslı kesintisiz güç kaynakları çözümleri.</p>
        </Link>

        <Link to="/cozumler" className="bg-white p-6 rounded-xl shadow hover:shadow-lg transition">
          <h2 className="text-xl font-semibold text-blue-700">Çözümler</h2>
          <p className="mt-2 text-gray-600">Size özel güç elektroniği çözümlerimiz için tıklayın.</p>
        </Link>

        <Link to="/iletisim" className="bg-white p-6 rounded-xl shadow hover:shadow-lg transition">
          <h2 className="text-xl font-semibold text-blue-700">İletişim</h2>
          <p className="mt-2 text-gray-600">Bize ulaşın ve destek alın.</p>
        </Link>
      </div>
    </div>
  );
};

export default Home;
