import React from 'react';
import { NavLink, Outlet } from 'react-router-dom';

const menu = [
  { path: 'ik-politikasi', label: 'İK Politikamız' },
  { path: 'genel-basvuru', label: 'Genel Başvuru Formu' },
  { path: 'staj-basvuru', label: 'Staj Başvuru Formu' },
];

export default function InsanKaynaklari() {
  return (
    <div className="flex flex-col md:flex-row max-w-4xl mx-auto mt-8 mb-16">
      <aside className="w-full md:w-1/3 border-r md:pr-6 mb-6 md:mb-0">
        <h2 className="text-2xl font-semibold text-gray-700 mb-4 border-b pb-2">İnsan Kaynakları</h2>
        <nav className="flex flex-col gap-2">
          {menu.map((item) => (
            <NavLink
              key={item.path}
              to={item.path}
              className={({ isActive }) =>
                `py-2 px-2 rounded transition text-blue-700 font-medium ${isActive ? 'bg-blue-100 border-l-4 border-blue-600' : 'hover:bg-blue-50'}`
              }
              end
            >
              {item.label}
            </NavLink>
          ))}
        </nav>
      </aside>
      <main className="flex-1 px-2 md:px-8">
        <Outlet />
      </main>
    </div>
  );
}
